			wbwide_zip_we,
			wbwide_i2cm_we,
			wbwide_i2cdma_we
		}),
		.i_maddr({
			wbwide_wbu_arbiter_addr,
			wbwide_zip_addr,
			wbwide_i2cm_addr,
			wbwide_i2cdma_addr
		}),
		.i_mdata({
