			wbwide_zip_data,
			wbwide_i2cm_data,
			wbwide_i2cdma_data
		}),
		.i_msel({
			wbwide_wbu_arbiter_sel,
			wbwide_zip_sel,
			wbwide_i2cm_sel,
			wbwide_i2cdma_sel
		}),
		.o_mstall({
			wbwide_wbu_arbiter_stall,
			wbwide_zip_stall,
			wbwide_i2cm_stall,
			wbwide_i2cdma_stall
		}),
		.o_mack({
			wbwide_wbu_arbiter_ack,
			wbwide_zip_ack,
			wbwide_i2cm_ack,
			wbwide_i2cdma_ack
		}),
		.o_mdata({
			wbwide_wbu_arbiter_idata,
